public func getCountryCode(_ selectedCode: String) -> String {
    var countryCodeToSend = ""
    for country in countryCodes {
        if country.contains(selectedCode) {
            countryCodeToSend = String(country.suffix(2))
            break
        }
    }
    return countryCodeToSend;
}
